import React from "react";
import { useState } from "react";

function Login(){
    const [email, setEmail] = useState("")
    const [password, setPassword] = useState("")

    function handleEmail(event){
        const value = event.target.value;
        const name = event.target.name
        setEmail({
            ...email,
            [name]: value
        })
    }

    function handlePassword(event){
        const value = event.target.value;
        const name  = event.target.name;
        setPassword({
            ...password,
            [name]: value
        })
    }

    function validation(){
        let result = true;
        if(email==="" || email===null){
          result = false;
          console.log("please Enter Email")
        }

        if(password==="" || password===null){
          result = false;
         console.log("please Enter Password")
        }
        return result;


      }

    function handleSumit(event){
        event.preventDefault()

        if(validation()){fetch("http://localhost:3000/users")
        .then(response =>response.json())
        .then(users =>{
            if(Object.keys(users).length === 0){
                console.log("Please enter email and password")
            }

            else{
                const userfound = users.find(user => user.password === password);
                sessionStorage.setItem("email", email)
                console.log(users.password)
                if(userfound){
                    console.log(`You have been logged successfully ${userfound.name}`)
                }else{
                    console.log("please provide valid email or password")
                }
            }
        })}
    }
    return(
     <>
     <div className="login-form">
     <h1>LOG IN</h1>

     <form onSubmit={handleSumit}>
       <div>
       <label>Email</label><br></br>
        <input 
        name="email"
        className="login-input"
        onChange={handleEmail}
        ></input>
       </div>
       <div>
       <label>Password</label><br></br>
        <input 
        type="password"
        onChange={handlePassword}
        className="login-input"
        name="password"
        ></input>
       </div>
       <div>
       <button className="login-button">Login</button>
       </div>
       </form>
     </div>


     </>
        )
}

export default Login;
